import logo from './logo.svg';
import './App.css';
import * as mui from '@mui/material';
import React, { useEffect, useState } from 'react';
import * as icon from '@mui/icons-material';
import { useMutation, useQuery } from '@apollo/client';
import { GET_ALL_USERS } from './queries/user';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import { ADD_USER, DELETE_USER } from './mutations/user';

const forbiddenSymbolsNumeric = ['KeyE', 'Period', 'Minus', 'Equal'];

function App() {
    const [username, setUsername] = useState('');
    const [age, setAge] = useState('');
    const [users, setUsers] = useState([]);
    const { data, loading, error, refetch } = useQuery(GET_ALL_USERS);
    const [addUser] = useMutation(ADD_USER);
    const [deleteUser] = useMutation(DELETE_USER);
    const [disabled, setDisabled] = useState(false);

    useEffect(() => {
        if (!loading) {
            setUsers(data.users);
        }
    }, [data]);

    const onSubmit = (e) => {
        e.preventDefault();
        setDisabled(true);
        addUser({
            variables: {
                user: {
                    username,
                    age: parseInt(age),
                },
            },
        })
            .then(({ data }) => {
                setUsername('');
                setAge('');
                let allUsers = Array.from(users);
                allUsers.push(data.addUser);
                setUsers(allUsers);
            })
            .finally(() => {
                setDisabled(false);
            });
    };
    const onChange = (e) => {
        if (e.target.name === 'age') {
            setAge(
                (() => {
                    if (e.target.value > 150) {
                        return 150;
                    } else {
                        return ((value) => {
                            const arr = value.split('');
                            for (let i = 0; i < arr.length; i++) {
                                if ((isNaN(arr[i - 1]) || arr[i - 1] === '0') && arr[i] === '0' && !isNaN(arr[i + 1])) {
                                    arr.splice(i, 1);
                                }
                            }

                            return arr.join('');
                        })(e.target.value);
                    }
                })()
            );
        } else if (e.target.name === 'username') {
            setUsername(e.target.value.trim());
        }
    };
    const onKeyDown = (e) => {
        if (e.target.tagName === 'INPUT' && e.target.type === 'number' && forbiddenSymbolsNumeric.includes(e.code)) {
            e.preventDefault();
        }
    };

    const onDelete = (e) => {
        e.preventDefault();
        setDisabled(true);

        deleteUser({
            variables: {
                id: e.target.closest('button').dataset.id,
            },
        })
            .then(({ data }) => {
                setUsers(Array.from(users).filter((user) => user.id != data.deleteUser.id));
            })
            .finally(() => {
                setDisabled(false);
            });
    };

    return (
        <>
            {(() => {
                debugger;
            })()}
            <mui.Box
                onSubmit={onSubmit}
                component="form"
                sx={{
                    display: 'flex',
                    alignItems: 'center',
                    '& .MuiTextField-root': { m: 1, width: '25ch' },
                }}
                noValidate
                autoComplete="off"
            >
                <mui.TextField name="username" label="Name" variant="filled" onInput={onChange} onChange={onChange} value={username} />
                <mui.TextField name="age" onKeyDown={onKeyDown} label="Age" variant="filled" type="number" onInput={onChange} onChange={onChange} value={age} />
                <mui.Button disabled={username.length === 0 || age.length === 0 || disabled} type="submit" variant="contained" endIcon={<icon.Send />}>
                    Send
                </mui.Button>
            </mui.Box>
            <mui.Box
                component="div"
                sx={{
                    '& .MuiTextField-root': { m: 1, width: '25ch' },
                }}
                className="d-flex justify-content-start align-items-center flex-wrap"
                style={{ maxWidth: '100%' }}
            >
                {!loading ? (
                    users.map((user) => (
                        <>
                            <mui.CardContent key={user.id} style={{ padding: 16 }}>
                                <div className="d-flex justify-content-between align-items-center" style={{ width: 'fit-content' }}>
                                    <div className="d-flex justify-content-between align-items-stretch" style={{ width: 'fit-content' }}>
                                        <mui.Typography className="d-flex align-items-end" variant="h5" component="div">
                                            {user.username}
                                        </mui.Typography>
                                        <mui.Typography className="d-flex align-items-end" color="text.secondary">
                                            {user.age}
                                        </mui.Typography>
                                    </div>
                                    <mui.CardActions>
                                        <mui.IconButton disabled={disabled} onClick={onDelete} aria-label="delete" color="error" data-id={user.id}>
                                            <icon.Delete />
                                        </mui.IconButton>
                                    </mui.CardActions>
                                </div>
                            </mui.CardContent>
                        </>
                    ))
                ) : (
                    <>
                        <mui.CardContent style={{ padding: 16 }}>
                            <div className="d-flex align-items-center justify-content-center" style={{ width: '100%' }}>
                                <div className="spinner-border" role="status">
                                    <span className="visually-hidden">Loading...</span>
                                </div>
                            </div>
                        </mui.CardContent>
                    </>
                )}
            </mui.Box>
        </>
    );
}

export default App;
