import logo from './logo.svg';
import './App.css';
import * as mui from '@mui/material';
import React from 'react';
import * as icon from '@mui/icons-material';

const forbiddenSymbolsNumeric = ['KeyE', 'Period', 'Minus', 'Equal'];

class App extends React.Component {
    constructor() {
        super();

        this.state = {
            username: '',
            age: '',
            canSend: true,
            users: [],
        };
    }
    onSubmit(e) {
        e.preventDefault();
    }
    onChange(e) {
        if (e.target.name === 'age') {
            this.setState({
                ...this.state,
                age: (() => {
                    if (e.target.value.includes('e') || e.target.value.includes('-')) {
                        return 0;
                    } else if (e.target.value > 150) {
                        return 150;
                    } else {
                        return e.target.value.replace(/\D|^0+/g, '');
                    }
                })(),
            });
        } else if (e.target.name === 'username') {
            this.setState({
                ...this.state,
                username: e.target.value.trim(),
            });
        }
    }
    onKeyDown(e) {
        if (e.target.tagName === 'INPUT' && e.target.type === 'number' && forbiddenSymbolsNumeric.includes(e.code)) {
            e.preventDefault();
        }
    }
    render() {
        return (
            <>
                <mui.Box
                    onSubmit={this.onSubmit.bind(this)}
                    style={{ display: 'flex', alignItems: 'center' }}
                    component="form"
                    sx={{
                        '& .MuiTextField-root': { m: 1, width: '25ch' },
                    }}
                    noValidate
                    autoComplete="off"
                >
                    <mui.TextField name="username" label="Name" variant="filled" onInput={this.onChange.bind(this)} onChange={this.onChange.bind(this)} value={this.state.username} />
                    <mui.TextField name="age" onKeyDown={this.onKeyDown.bind(this)} label="Age" variant="filled" type="number" onInput={this.onChange.bind(this)} onChange={this.onChange.bind(this)} value={this.state.age} />
                    <mui.Button disabled={this.state.username.length === 0 || this.state.age.length === 0} type="submit" variant="contained" endIcon={<icon.Send />}>
                        Send
                    </mui.Button>
                </mui.Box>
                <mui.Box
                    component="div"
                    sx={{
                        '& .MuiTextField-root': { m: 1, width: '25ch' },
                    }}
                >
                    <mui.CardContent>
                        <mui.Typography sx={{ fontSize: 14 }} color="text.secondary" gutterBottom>
                            Word of the Day
                        </mui.Typography>
                        <mui.Typography variant="h5" component="div">
                            be
                        </mui.Typography>
                        <mui.Typography sx={{ mb: 1.5 }} color="text.secondary">
                            adjective
                        </mui.Typography>
                        <mui.Typography variant="body2">
                            well meaning and kindly.
                            <br />
                            {'"a benevolent smile"'}
                        </mui.Typography>
                    </mui.CardContent>
                    <mui.CardActions>
                        <mui.IconButton aria-label="delete" color="danger">
                            <icon.Delete />
                        </mui.IconButton>
                    </mui.CardActions>
                </mui.Box>
            </>
        );
    }
}

export default App;
