import logo from './logo.svg';
import './App.css';
import * as mui from '@mui/material';
import React from 'react';
import * as icon from '@mui/icons-material';

const forbiddenSymbolsNumeric = ['KeyE', 'Period', 'Minus', 'Equal'];

class App extends React.Component {
    constructor() {
        super();

        this.state = {
            username: '',
            age: '',
            canSend: true,
        };
    }
    onSubmit(e) {
        e.preventDefault();
    }
    onChange(e) {
        console.log(e.target.name);
        this.setState({
            ...this.state,
            age: (() => {
                if (e.target.value.includes('e') || e.target.value.includes('-')) {
                    return 0;
                } else if (e.target.value > 150) {
                    return 150;
                } else {
                    return e.target.value.replace(/\D|^0+/g, '');
                }
            })(),
        });
    }
    onKeyDown(e) {
        if (e.target.tagName === 'INPUT' && e.target.type === 'number' && forbiddenSymbolsNumeric.includes(e.code)) {
            e.preventDefault();
        }
    }
    render() {
        return (
            <mui.Box
                onSubmit={this.onSubmit.bind(this)}
                style={{ display: 'flex', alignItems: 'center' }}
                component="form"
                sx={{
                    '& .MuiTextField-root': { m: 1, width: '25ch' },
                }}
                noValidate
                autoComplete="off"
            >
                <mui.TextField label="Name" variant="filled" />
                <mui.TextField onKeyDown={this.onKeyDown.bind(this)} label="Age" variant="filled" type="number" onInput={this.onChange.bind(this)} onChange={this.onChange.bind(this)} value={this.state.age} />
                <mui.Button disabled={this.state.username.length === 0 || this.state.age.length === 0} type="submit" variant="contained" endIcon={<icon.Send />}>
                    Send
                </mui.Button>
            </mui.Box>
        );
    }
}

export default App;
