import { gql } from '@apollo/client';

export const ADD_USER = gql`
    mutation addUser($user: UserInput) {
        addUser(user: $user) {
            username
            age
        }
    }

    mutation deleteUser($id: ID) {
        deleteUser(id: ID) {
            id
        }
    }
`;
