import logo from './logo.svg';
import './App.css';
import * as mui from '@mui/material';
import React, { useEffect, useState } from 'react';
import * as icon from '@mui/icons-material';
import { useQuery } from '@apollo/client';
import { GET_ALL_USERS } from './query/user';

const forbiddenSymbolsNumeric = ['KeyE', 'Period', 'Minus', 'Equal'];

function App() {
    const [username, setUsername] = useState('');
    const [age, setAge] = useState('');
    const [users, setUsers] = useState([]);
    const { data, loading, error } = useQuery(GET_ALL_USERS);

    useEffect(() => {
        if (!loading) {
            setUsers(data.users);
        }
    }, [data]);

    const onSubmit = (e) => {
        e.preventDefault();
    };
    const onChange = (e) => {
        if (e.target.name === 'age') {
            setAge(
                (() => {
                    if (e.target.value.includes('e') || e.target.value.includes('-')) {
                        return 0;
                    } else if (e.target.value > 150) {
                        return 150;
                    } else {
                        return e.target.value.replace(/\D|^0+/g, '');
                    }
                })()
            );
        } else if (e.target.name === 'username') {
            setUsername(e.target.value.trim());
        }
    };
    const onKeyDown = (e) => {
        if (e.target.tagName === 'INPUT' && e.target.type === 'number' && forbiddenSymbolsNumeric.includes(e.code)) {
            e.preventDefault();
        }
    };
    return (
        <>
            <mui.Box
                onSubmit={onSubmit}
                component="form"
                sx={{
                    display: 'flex',
                    alignItems: 'center',
                    '& .MuiTextField-root': { m: 1, width: '25ch' },
                }}
                noValidate
                autoComplete="off"
            >
                <mui.TextField name="username" label="Name" variant="filled" onInput={onChange} onChange={onChange} value={username} />
                <mui.TextField name="age" onKeyDown={onKeyDown} label="Age" variant="filled" type="number" onInput={onChange} onChange={onChange} value={age} />
                <mui.Button disabled={username.length === 0 || age.length === 0} type="submit" variant="contained" endIcon={<icon.Send />}>
                    Send
                </mui.Button>
            </mui.Box>
            <mui.Box
                component="div"
                sx={{
                    '& .MuiTextField-root': { m: 1, width: '25ch' },
                }}
            >
                {users.map((user) => (
                    <>
                        <mui.CardContent key={user.id}>
                            <mui.Typography sx={{ fontSize: 14 }} color="text.secondary" gutterBottom>
                                Word of the Day
                            </mui.Typography>
                            <mui.Typography variant="h5" component="div">
                                be
                            </mui.Typography>
                            <mui.Typography sx={{ mb: 1.5 }} color="text.secondary">
                                adjective
                            </mui.Typography>
                            <mui.Typography variant="body2">
                                well meaning and kindly.
                                <br />
                                {'"a benevolent smile"'}
                            </mui.Typography>
                        </mui.CardContent>
                        <mui.CardActions>
                            <mui.IconButton aria-label="delete" color="error">
                                <icon.Delete />
                            </mui.IconButton>
                        </mui.CardActions>
                    </>
                ))}
            </mui.Box>
        </>
    );
}

export default App;
