import logo from './logo.svg';
import './App.css';
import * as mui from '@mui/material';
import React from 'react';

class App extends React.Component {
    constructor() {
        super();

        this.state = {
            username: '',
            age: '',
        };
    }
    onChange(e) {
        this.setState({
            ...this.state,
            age: (() => {
                if (e.target.value.includes('e') || e.target.value.includes('-')) {
                    return 0;
                } else if (e.target.value.length === 0) {
                    console.log(123);

                    return '';
                } else {
                    return e.target.value;
                }
            })(),
        });
        this.forceUpdate();
    }
    render() {
        return (
            <mui.Box
                component="form"
                sx={{
                    '& .MuiTextField-root': { m: 1, width: '25ch' },
                }}
                noValidate
                autoComplete="off"
            >
                <mui.TextField helperText={'Enter a your name'} label="Name" variant="filled" />
                <mui.TextField helperText={'Enter a your age'} label="Age" variant="filled" type="number" onInput={this.onChange.bind(this)} onChange={this.onChange.bind(this)} value={this.state.age} />
            </mui.Box>
        );
    }
}

export default App;
