import { gql } from '@apollo/client';

export const ADD_USER = gql`
    mutation addUser2($user: UserInput) {
        addUser2(user: $user) {
            id
            username
            age
        }
    }
`;

export const DELETE_USER = gql`
    mutation deleteUser($id: ID) {
        deleteUser(id: $id) {
            id
        }
    }
`;
