import logo from './logo.svg';
import './App.css';
import * as mui from '@mui/material';
import React, { useEffect, useState } from 'react';
import * as icon from '@mui/icons-material';
import { useQuery } from '@apollo/client';
import { GET_ALL_USERS } from './query/user';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';

const forbiddenSymbolsNumeric = ['KeyE', 'Period', 'Minus', 'Equal'];

function App() {
    const [username, setUsername] = useState('');
    const [age, setAge] = useState('');
    const [users, setUsers] = useState([]);
    const { data, loading, error } = useQuery(GET_ALL_USERS);

    useEffect(() => {
        if (!loading) {
            setUsers(data.users);
        }
    }, [data]);

    const onSubmit = (e) => {
        e.preventDefault();
    };
    const onChange = (e) => {
        if (e.target.name === 'age') {
            setAge(
                (() => {
                    if (e.target.value.includes('e') || e.target.value.includes('-')) {
                        return 0;
                    } else if (e.target.value > 150) {
                        return 150;
                    } else {
                        return e.target.value.replace(/\D|^0+/g, '');
                    }
                })()
            );
        } else if (e.target.name === 'username') {
            setUsername(e.target.value.trim());
        }
    };
    const onKeyDown = (e) => {
        if (e.target.tagName === 'INPUT' && e.target.type === 'number' && forbiddenSymbolsNumeric.includes(e.code)) {
            e.preventDefault();
        }
    };

    if (loading) return '';

    return (
        <>
            <mui.Box
                onSubmit={onSubmit}
                component="form"
                sx={{
                    display: 'flex',
                    alignItems: 'center',
                    '& .MuiTextField-root': { m: 1, width: '25ch' },
                }}
                noValidate
                autoComplete="off"
            >
                <mui.TextField name="username" label="Name" variant="filled" onInput={onChange} onChange={onChange} value={username} />
                <mui.TextField name="age" onKeyDown={onKeyDown} label="Age" variant="filled" type="number" onInput={onChange} onChange={onChange} value={age} />
                <mui.Button disabled={username.length === 0 || age.length === 0} type="submit" variant="contained" endIcon={<icon.Send />}>
                    Send
                </mui.Button>
            </mui.Box>
            <mui.Box
                component="div"
                sx={{
                    '& .MuiTextField-root': { m: 1, width: '25ch' },
                }}
                className="d-flex justify-content-start align-items-center"
            >
                {users.map((user) => (
                    <>
                        <mui.CardContent key={user.id}>
                            <div className="d-flex justify-content-between align-items-center" style={{ width: 'fit-content' }}>
                                <div className="d-flex justify-content-between align-items-stretch" style={{ width: 'fit-content' }}>
                                    <mui.Typography className="d-flex align-items-end" variant="h5" component="div">
                                        {user.username}
                                    </mui.Typography>
                                    <mui.Typography className="d-flex align-items-end" color="text.secondary">
                                        {user.age}
                                    </mui.Typography>
                                </div>
                                <mui.CardActions>
                                    <mui.IconButton aria-label="delete" color="error">
                                        <icon.Delete />
                                    </mui.IconButton>
                                </mui.CardActions>
                            </div>
                        </mui.CardContent>
                    </>
                ))}
            </mui.Box>
        </>
    );
}

export default App;
