// import logo from './logo.svg';
import './App.css';
import * as mui from '@mui/material';
import React, { useEffect, useState } from 'react';
import * as icon from '@mui/icons-material';
import { useMutation, useQuery } from '@apollo/client';
import { GET_ALL_USERS } from './queries/user';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import { ADD_USER, DELETE_USER } from './mutations/user';
import { ADD_TODO, DELETE_TODO } from './mutations/todo';

const forbiddenSymbolsNumeric = ['KeyE', 'Period', 'Minus', 'Equal'];

function App() {
    const [username, setUsername] = useState('');
    const [age, setAge] = useState('');
    const [users, setUsers] = useState([]);

    const { data, loading, error, refetch } = useQuery(GET_ALL_USERS);
    const [addUser] = useMutation(ADD_USER);
    const [deleteUser] = useMutation(DELETE_USER);
    const [addTodo] = useMutation(ADD_TODO);

    const [disabled, setDisabled] = useState(false);
    const [renderUsers, setRenderUsers] = useState(false);
    const [errLoad, setErrLoad] = useState('');
    const [refreshDisabled, setRefreshDisabled] = useState(false);
    const [actionError, setActionError] = useState('');
    const [open, setOpen] = useState(false);
    const [nameInput, setNameInput] = useState(null);

    useEffect(() => {
        if (!loading) {
            setErrLoad('');
            setUsers(data.users);
            setRenderUsers(true);
        }
    }, [data]); // eslint-disable-line react-hooks/exhaustive-deps
    useEffect(() => {
        if (error) {
            setErrLoad(error.message);
        }
    }, [error]); // eslint-disable-line react-hooks/exhaustive-deps
    const onAddTodo = (e, userId) => {
        e.preventDefault();
        setDisabled(true);
        addTodo({
            variables: {
                todo: {
                    text: '',
                    userId,
                },
            },
        })
            .then(({ data }) => {
                let allUsers = Array.from(users);
                let user = allUsers.find((user) => user.id === userId);
                user.todos.push(data.addTodo);
                setUsers(allUsers);
            })
            .catch((err) => {
                setActionError(err.message);
                setOpen(true);
            })
            .finally(() => {
                setDisabled(false);
            });
    };
    const onAdd = (e) => {
        e.preventDefault();
        setDisabled(true);
        addUser({
            variables: {
                user: {
                    username,
                    age: parseInt(age),
                },
            },
        })
            .then(({ data }) => {
                setUsername('');
                setAge('');
                let allUsers = Array.from(users);
                allUsers.push(data.addUser);
                setUsers(allUsers);
                nameInput.focus();
            })
            .catch((err) => {
                setActionError(err.message);
                setOpen(true);
            })
            .finally(() => {
                setDisabled(false);
            });
    };

    const onDelete = (e, userId) => {
        e.preventDefault();
        setDisabled(true);
        deleteUser({
            variables: {
                id: userId,
            },
        })
            .then(({ data }) => {
                setUsers(Array.from(users).filter((user) => user.id !== data.deleteUser.id));
            })
            .catch((err) => {
                setActionError(err.message);
                setOpen(true);
            })
            .finally(() => {
                setDisabled(false);
            });
    };

    const onChange = (e) => {
        if (e.target.name === 'age') {
            setAge(
                (() => {
                    if (e.target.value > 150) {
                        return 150;
                    } else {
                        return ((value) => {
                            const arr = value.split('');
                            for (let i = 0; i < arr.length; i++) {
                                if ((isNaN(arr[i - 1]) || arr[i - 1] === '0') && arr[i] === '0' && !isNaN(arr[i + 1])) {
                                    arr.splice(i, 1);
                                }
                            }

                            return arr.join('');
                        })(e.target.value);
                    }
                })()
            );
        } else if (e.target.name === 'username') {
            setUsername(e.target.value.trim());
        }
    };
    const onKeyDown = (e) => {
        if (e.target.tagName === 'INPUT' && e.target.type === 'number' && forbiddenSymbolsNumeric.includes(e.code)) {
            e.preventDefault();
        }
    };

    const reload = (e) => {
        e.preventDefault();
        setRefreshDisabled(true);
        refetch()
            .catch((err) => {})
            .finally(() => {
                setRefreshDisabled(false);
            });
    };

    const toastClose = (e) => {
        setOpen(false);
    };

    return (
        <div className="d-flex justify-content-center flex-wrap align-items-center">
            {!errLoad ? (
                <>
                    {/* {(() => {
                debugger;
            })()} */}
                    <mui.Box
                        onSubmit={onAdd}
                        component="form"
                        sx={{
                            width: '100%',
                            display: 'flex',
                            alignItems: 'center',
                            justifyContent: 'center',
                            '& .MuiTextField-root': { m: 1, width: '25ch' },
                        }}
                        noValidate
                        autoComplete="off"
                    >
                        <mui.TextField
                            inputRef={(input) => {
                                setNameInput(input);
                            }}
                            name="username"
                            label="Name"
                            variant="filled"
                            onInput={onChange}
                            onChange={onChange}
                            value={username}
                        />
                        <mui.TextField name="age" onKeyDown={onKeyDown} label="Age" variant="filled" type="number" onInput={onChange} onChange={onChange} value={age} />
                        <mui.Button aria-label="add user" disabled={username.length === 0 || age.length === 0 || disabled} type="submit" variant="contained" endIcon={<icon.Add />}>
                            Add
                        </mui.Button>
                        <mui.Snackbar open={open} autoHideDuration={6000} onClose={toastClose}>
                            <mui.Alert onClose={toastClose} severity="error" sx={{ width: '100%' }}>
                                {actionError}
                            </mui.Alert>
                        </mui.Snackbar>
                    </mui.Box>
                    <mui.Box
                        component="div"
                        sx={{
                            '& .MuiTextField-root': { m: 1, width: '25ch' },
                        }}
                        className="d-flex justify-content-start align-items-center flex-wrap user-cards"
                    >
                        {!loading && renderUsers ? (
                            users.length > 0 ? (
                                users.map((user) => (
                                    <div key={user.id} className="user-card">
                                        <mui.Paper elevation={4}>
                                            <mui.CardContent style={{ position: 'relative', padding: 16 }}>
                                                <mui.IconButton
                                                    disabled={disabled}
                                                    onClick={(e) => {
                                                        onDelete(e, user.id);
                                                    }}
                                                    aria-label="delete user"
                                                    color="error"
                                                    style={{ position: 'absolute', right: 0, top: 0 }}
                                                >
                                                    <icon.Delete />
                                                </mui.IconButton>
                                                <mui.Typography className="d-flex align-items-end mt-4" variant="h6" component="div">
                                                    Name: {user.username}
                                                </mui.Typography>
                                                <mui.Typography sx={{ fontSize: 14 }} color="text.secondary" gutterBottom>
                                                    age: {user.age}
                                                </mui.Typography>

                                                <mui.Typography sx={{ mt: 4, mb: 2 }} variant="h4" component="div">
                                                    Todos
                                                    <mui.IconButton
                                                        onClick={(e) => {
                                                            onAddTodo(e, user.id);
                                                        }}
                                                        aria-label="add todo"
                                                        disabled={disabled}
                                                        color="success"
                                                    >
                                                        <icon.Add></icon.Add>
                                                    </mui.IconButton>
                                                </mui.Typography>
                                                {user.todos.length > 0 ? (
                                                    user.todos.map((todo) => (
                                                        <mui.List>
                                                            <mui.ListItem
                                                                key={todo.id}
                                                                secondaryAction={
                                                                    <>
                                                                        <mui.IconButton color="primary" edge="end" aria-label="edit todo">
                                                                            <icon.Edit />
                                                                        </mui.IconButton>
                                                                        <mui.IconButton edge="end" aria-label="delete todo">
                                                                            <icon.Delete />
                                                                        </mui.IconButton>
                                                                    </>
                                                                }
                                                                disablePadding
                                                            >
                                                                <mui.ListItemButton dense style={{ paddingRight: 90, width: '100%' }}>
                                                                    <mui.ListItemIcon style={{ minWidth: 'unset' }}>
                                                                        <mui.Checkbox edge="start" checked={todo.done} tabIndex={-1} disableRipple />
                                                                    </mui.ListItemIcon>
                                                                    <mui.TextField multiline className="todo-text" disabled={true} variant="standard" value={todo.text} />
                                                                    {/* <mui.ListItemText primary={} /> */}
                                                                </mui.ListItemButton>
                                                            </mui.ListItem>
                                                        </mui.List>
                                                    ))
                                                ) : (
                                                    <div>No todos yet</div>
                                                )}
                                            </mui.CardContent>
                                        </mui.Paper>
                                    </div>
                                ))
                            ) : (
                                <div className="d-flex justify-content-center align-items-center" style={{ width: '100%' }}>
                                    <mui.CardContent style={{ padding: 16 }}>
                                        <span>No users yet</span>
                                    </mui.CardContent>
                                </div>
                            )
                        ) : (
                            <div className="d-flex justify-content-center align-items-center" style={{ width: '100%' }}>
                                <mui.CardContent style={{ padding: 16 }}>
                                    <div className="spinner-border" role="status">
                                        <span className="visually-hidden">Loading...</span>
                                    </div>
                                </mui.CardContent>
                            </div>
                        )}
                    </mui.Box>
                </>
            ) : (
                <mui.Alert
                    severity="error"
                    action={
                        <mui.Button disabled={refreshDisabled} onClick={reload} color="inherit" size="small">
                            Refresh
                        </mui.Button>
                    }
                >
                    {errLoad}
                </mui.Alert>
            )}
        </div>
    );
}

export default App;
